import React, { useState, useEffect } from "react";
import "./WeatherComponent.css";

const WeatherComponent = () => {
  const [city, setCity] = useState("London");
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchWeatherData = async () => {
      try {
        const API_KEY = process.env.REACT_APP_OPENWEATHER_API_KEY;
        const response = await fetch(
          `https://api.openweathermap.org/data/2.5/forecast?q=${city}&appid=${API_KEY}`
        );
        if (!response.ok) {
          throw new Error(
            `Network response was not ok: ${response.statusText}`
          );
        }
        const data = await response.json();
        setWeatherData(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchWeatherData();
  }, [city]);

  const handleCityChange = (e) => {
    setCity(e.target.value);
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loader"></div>
        <p>Loading weather data...</p>
      </div>
    );
  }

  if (error) {
    return <div className="error-container">Error: {error}</div>;
  }

  return (
    <div className="weather-container">
      <form onSubmit={handleFormSubmit} className="search-form">
        <input
          type="text"
          value={city}
          onChange={handleCityChange}
          placeholder="Enter city name"
          className="city-input"
        />
        <button type="submit" className="search-button">
          Get Weather
        </button>
      </form>

      <h1 className="city-title">5-Day Weather Forecast in {weatherData.city.name}</h1>

      <div className="forecast-grid">
        {weatherData.list.slice(0, 5).map((forecast, index) => (
          <div key={index} className="forecast-card">
            <h2>{new Date(forecast.dt * 1000).toLocaleDateString()}</h2>
            <img
              src={`http://openweathermap.org/img/wn/${forecast.weather[0].icon}@2x.png`}
              alt={forecast.weather[0].description}
              className="weather-icon"
            />
            <div className="temperature">
              <p className="temp-main">{(forecast.main.temp - 273.15).toFixed(1)}°C</p>
              <p className="temp-feel">Feels like: {(forecast.main.feels_like - 273.15).toFixed(1)}°C</p>
            </div>
            <div className="weather-details">
              <p>{forecast.weather[0].description}</p>
              <p>Humidity: {forecast.main.humidity}%</p>
              <p>Wind: {forecast.wind.speed} m/s</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default WeatherComponent;
