import React, { useState, useEffect } from "react";
import "./WeatherComponent.css";

const API_KEY = "1635890035cbba097fd5c26c8ea672a1"; // Updated API key
const BASE_URL = "https://api.openweathermap.org/data/2.5/weather"; // Using current weather API first to test

const WeatherComponent = () => {
  const [city, setCity] = useState("");
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchWeatherData = async (cityName) => {
    if (!cityName.trim()) {
      setError("Please enter a city name");
      return;
    }

    try {
      setLoading(true);
      setError(null);

      const response = await fetch(
        `${BASE_URL}?q=${encodeURIComponent(cityName.trim())}&appid=${API_KEY}&units=metric`
      );

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(
          errorData.message || `Error: ${response.status} ${response.statusText}`
        );
      }

      const data = await response.json();
      console.log('Weather data:', data);
      setWeatherData(data);
      setError(null);
    } catch (error) {
      console.error('Fetch error:', error);
      setError(error.message);
      setWeatherData(null);
    } finally {
      setLoading(false);
    }
  };

  const handleCityChange = (e) => {
    setCity(e.target.value);
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    fetchWeatherData(city);
  };

  return (
    <div className="weather-container">
      <form onSubmit={handleFormSubmit} className="search-form">
        <input
          type="text"
          value={city}
          onChange={handleCityChange}
          placeholder="Enter city name (e.g., London)"
          className="city-input"
          required
          minLength={2}
        />
        <button 
          type="submit" 
          className="search-button"
          disabled={!city.trim() || loading}
        >
          {loading ? 'Loading...' : 'Get Weather'}
        </button>
      </form>

      {error && (
        <div className="error-container">
          <p>Error: {error}</p>
          <p>Please check if:</p>
          <ul>
            <li>The city name is spelled correctly</li>
            <li>You have entered a valid city name</li>
            <li>You have a stable internet connection</li>
          </ul>
        </div>
      )}

      {loading && (
        <div className="loading-container">
          <div className="loader"></div>
          <p>Loading weather data...</p>
        </div>
      )}

      {weatherData && !loading && !error && (
        <>
          <h1 className="city-title">Current Weather in {weatherData.name}</h1>

          <div className="forecast-grid">
            <div className="forecast-card">
              <h2>{new Date().toLocaleDateString()}</h2>
              <img
                src={`https://openweathermap.org/img/wn/${weatherData.weather[0].icon}@2x.png`}
                alt={weatherData.weather[0].description}
                className="weather-icon"
              />
              <div className="temperature">
                <p className="temp-main">{weatherData.main.temp.toFixed(1)}°C</p>
                <p className="temp-feel">Feels like: {weatherData.main.feels_like.toFixed(1)}°C</p>
              </div>
              <div className="weather-details">
                <p>{weatherData.weather[0].description}</p>
                <p>Humidity: {weatherData.main.humidity}%</p>
                <p>Wind: {weatherData.wind.speed} m/s</p>
              </div>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default WeatherComponent;
